using namespace std;

class Solution {
   public:
    int distributeCandies(int n, int limit) {
        vector<vector<int>> dp(4, vector<int>(n + 1));
        vector<vector<int>> pref(4, vector<int>(n + 1));
        dp[0][0] = 1;
        for (int i = 0; i <= n; i++) {
            pref[0][i] = 1;
        }
        for (int i = 1; i <= 3; i++) {
            for (int j = 0; j <= min(n, i * limit); j++) {
                dp[i][j] += pref[i - 1][j];
                if (j - limit > 0) {
                    dp[i][j] -= pref[i - 1][j - limit - 1];
                }
            }
            pref[i][0] = dp[i][0];
            for (int j = 1; j <= n; j++) {
                pref[i][j] = pref[i][j - 1] + dp[i][j];
            }
        }
        return dp[3][n];
    }
};

