class Solution {
 public:
  vector<int> resultArray(vector<int>& nums) {
    vector<int> v1 = {nums[0]};
    vector<int> v2 = {nums[1]};
    for (int i = 2; i < (int)nums.size(); i++) {
      if (v1.back() > v2.back()) {
        v1.push_back(nums[i]);
      } else {
        v2.push_back(nums[i]);
      }
    }
    vector<int> ans;
    for (auto& e : v1) {
      ans.push_back(e);
    }
    for (auto& e : v2) {
      ans.push_back(e);
    }
    return ans;
  }
};

