class Solution {
   public:
    bool isSubstringPresent(string s) {
        string t = s;
        reverse(t.begin(), t.end());
        for (int i = 0; i < (int)s.size() - 1; i++) {
            if (t.find(s.substr(i, 2)) != string::npos) {
                return true;
            }
        }
        return false;
    }
};

