using namespace std;

class Solution {
   public:
    int cnt[1000005];

    long long numberOfPairs(vector<int>& nums1, vector<int>& nums2, int k) {
        memset(cnt, 0, sizeof(cnt));
        map<int, int> mp;

        for (auto& e : nums1) {
            cnt[e]++;
        }
        for (auto& e : nums2) {
            mp[e]++;
        }
        long long ans = 0;
        for (auto& e : mp) {
            int x = e.first * k;
            for (int i = x; i <= 1000000; i += x) {
                ans += 1LL * e.second * cnt[i];
            }
        }
        return ans;
    }
};

