class Solution {
   public:
    int countDays(int days, vector<vector<int>>& meetings) {
        ranges::sort(meetings);
        int ans = (meetings.front()[0] - 1);
        int mx = meetings.front()[1];
        for (int i = 1; i < (int)meetings.size(); i++) {
            if (meetings[i][0] > mx) {
                ans += meetings[i][0] - mx - 1;
            }
            mx = max(mx, meetings[i][1]);
        }
        if (days > mx) {
            ans += days - mx;
        }
        return ans;
    }
};

